        public static readonly string pickABrickQuery = "query PickABrickQuery($query: String, $page: Int, $perPage: Int, $sort: SortInput, $includeOutOfStock: Boolean, $filters: [Filter!]) {\n  __typename\n  elements(\n    query: $query\n    page: $page\n    perPage: $perPage\n    filters: $filters\n    includeOutOfStock: $includeOutOfStock\n    sort: $sort\n  ) {\n    count\n    facets {\n      ...FacetData\n      __typename\n    }\n    sortOptions {\n      ...Sort_SortOptions\n      __typename\n    }\n    results {\n      ...ElementLeafData\n      __typename\n    }\n    total\n    __typename\n  }\n}\n\nfragment FacetData on Facet {\n  id\n  key\n  name\n  labels {\n    count\n    key\n    name\n    ... on FacetValue {\n      value\n      __typename\n    }\n    ... on FacetRange {\n      from\n      to\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment Sort_SortOptions on SortOptions {\n  id\n  key\n  direction\n  label\n  __typename\n}\n\nfragment ElementLeafData on Element {\n  id\n  name\n  categories {\n    name\n    key\n    parent {\n      name\n      __typename\n    }\n    __typename\n  }\n  spinsetMedia {\n    frames {\n      url\n      __typename\n    }\n    __typename\n  }\n  inStock\n  ... on SingleVariantElement {\n    variant {\n      ...ElementLeafVariant\n      __typename\n    }\n    __typename\n  }\n  ... on MultiVariantElement {\n    variants {\n      ...ElementLeafVariant\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment ElementLeafVariant on ElementVariant {\n  id\n  price {\n    centAmount\n    formattedAmount\n    __typename\n  }\n  attributes {\n    designNumber\n    colourId\n    deliveryChannel\n    __typename\n  }\n  __typename\n}";
        public static readonly string productSearchQuery = "query SearchQuery($q: String!, $page: Int, $perPage: Int, $filters: [Filter!], $sort: SortInput, $isPaginated: Boolean!, $visibility: ProductVisibility, $scoreFactorAttribute: String, $scoreFactorModifier: String, $scoreFactorMultiplier: String, $scoreFactorBoostMode: String, $hideTargetedSections: Boolean) { search(query: $q, visibility: $visibility) { ... on RedirectAction { __typename url } ... on ProductSearchResults { __typename productResult(page: $page, perPage: $perPage, filters: $filters, sort: $sort, scoring: {scoreFactorAttribute: $scoreFactorAttribute, scoreFactorModifier: $scoreFactorModifier, scoreFactorMultiplier: $scoreFactorMultiplier, scoreFactorBoostMode: $scoreFactorBoostMode}) @include(if: $isPaginated) { ...Search_ProductResults __typename } productResult(page: $page, perPage: $perPage, filters: $filters, sort: $sort, scoring: {scoreFactorAttribute: $scoreFactorAttribute, scoreFactorModifier: $scoreFactorModifier, scoreFactorMultiplier: $scoreFactorMultiplier, scoreFactorBoostMode: $scoreFactorBoostMode}) @skip(if: $isPaginated) { ...Search_ProductResults __typename } resultFor noResultContent { title overrideCopy sections { ... on LayoutSection { ...SearchLayoutSection __typename } ...SearchContentSections ... on ProductCarouselSection { ...ProductCarousel_UniqueFields productCarouselProducts: products(page: 1, perPage: 16, sort: {key: FEATURED, direction: DESC}) { ...Product_ProductItem __typename } __typename } ... on SplitTestingSection { variantId testId optimizelyEntityId inExperimentAudience hidden(hideTargetedSections: $hideTargetedSections) section { ... on LayoutSection { ...SearchLayoutSection __typename } ...SearchContentSections ... on ProductCarouselSection { ...ProductCarousel_UniqueFields productCarouselProducts: products(page: 1, perPage: 16, sort: {key: FEATURED, direction: DESC}) { ...Product_ProductItem __typename } __typename } __typename } __typename } ... on TargetedSection { hidden(hideTargetedSections: $hideTargetedSections) section { ... on LayoutSection { ...SearchLayoutSection __typename } ...SearchContentSections ... on ProductCarouselSection { ...ProductCarousel_UniqueFields productCarouselProducts: products(page: 1, perPage: 16, sort: {key: FEATURED, direction: DESC}) { ...Product_ProductItem __typename } __typename } __typename } __typename } __typename } __typename } } __typename } } fragment Search_ProductResults on ProductQueryResult { __typename count total results { ...Product_ProductItem __typename } facets { ...Facet_FacetSidebar __typename } sortOptions { ...Sort_SortOptions __typename } } fragment Product_ProductItem on Product { __typename id productCode name slug primaryImage(size: THUMBNAIL) baseImgUrl: primaryImage overrideUrl ... on ReadOnlyProduct { readOnlyVariant { ...Variant_ReadOnlyProduct __typename } __typename } ... on SingleVariantProduct { variant { ...Variant_ListingProduct __typename } __typename } ... on MultiVariantProduct { variants { ...Variant_ListingProduct __typename } __typename } } fragment Variant_ListingProduct on ProductVariant { id sku salePercentage attributes { rating maxOrderQuantity availabilityStatus availabilityText vipAvailabilityStatus vipAvailabilityText canAddToBag canAddToWishlist vipCanAddToBag onSale isNew ...ProductAttributes_Flags __typename } ...ProductVariant_Pricing __typename } fragment ProductVariant_Pricing on ProductVariant { price { formattedAmount centAmount currencyCode formattedValue __typename } listPrice { formattedAmount centAmount __typename } attributes { onSale __typename } __typename } fragment ProductAttributes_Flags on ProductAttributes { featuredFlags { key label __typename } __typename } fragment Variant_ReadOnlyProduct on ReadOnlyVariant { id sku attributes { featuredFlags { key label __typename } __typename } __typename } fragment Facet_FacetSidebar on Facet { name key id labels { __typename displayMode name labelKey count ... on FacetValue { value __typename } ... on FacetRange { from to __typename } } __typename } fragment Sort_SortOptions on SortOptions { id key direction label __typename } fragment ListingBanner on ListingBannerSection { title description contrast logoImage images { size url __typename } backgroundImages { small { url width height maxPixelDensity format __typename } medium { url width height maxPixelDensity format __typename } large { url width height maxPixelDensity format __typename } __typename } __typename } fragment CardContent on CardContentSection { moduleTitle showModuleTitle blocks { title isH1 description textAlignment logo secondaryLogo primaryLogoSrc { url width height maxPixelDensity format __typename } secondaryLogoSrc { url width height maxPixelDensity format __typename } logoPosition image imageSrc { url width height maxPixelDensity format __typename } callToActionText callToActionLink altText contrast __typename } __typename } fragment CopySectionData on CopySection { title showTitle body __typename } fragment QuickLinksData on QuickLinkSection { title quickLinks { title isH1 image link openInNewTab contrast imageSrc { url width height maxPixelDensity format __typename } __typename } __typename } fragment ContentBlockMixedData on ContentBlockMixed { moduleTitle showModuleTitle blocks { title isH1 description backgroundColor blockTheme contentPosition logoURL logoPosition callToActionText callToActionLink altText backgroundImages { largeImage { small { url width height maxPixelDensity format __typename } large { url width height maxPixelDensity format __typename } __typename } smallImage { small { url width height maxPixelDensity format __typename } large { url width height maxPixelDensity format __typename } __typename } __typename } __typename } __typename } fragment HeroBannerData on HeroBanner { heroblocks { id title isH1 tagline bannerTheme contentVerticalPosition contentHorizontalPosition contentHeight primaryLogoSrc primaryLogoSrcNew { url width height max