etedSection { hidden(hideTargetedSections: $hideTargetedSections) section { ... on LayoutSection { ...SearchLayoutSection __typename } ...SearchContentSections ... on ProductCarouselSection { ...ProductCarousel_UniqueFields productCarouselProducts: products(page: 1, perPage: 16, sort: {key: FEATURED, direction: DESC}) { ...Product_ProductItem __typename } __typename } __typename } __typename } __typename } __typename } } __typename } } fragment Search_ProductResults on ProductQueryResult { __typename count total results { ...Product_ProductItem __typename } facets { ...Facet_FacetSidebar __typename } sortOptions { ...Sort_SortOptions __typename } } fragment Product_ProductItem on Product { __typename id productCode name slug primaryImage(size: THUMBNAIL) baseImgUrl: primaryImage overrideUrl ... on ReadOnlyProduct { readOnlyVariant { ...Variant_ReadOnlyProduct __typename } __typename } ... on SingleVariantProduct { variant { ...Variant_ListingProduct __typename } __typename } ... on MultiVariantProduct { variants { ...Variant_ListingProduct __typename } __typename } } fragment Variant_ListingProduct on ProductVariant { id sku salePercentage attributes { rating maxOrderQuantity availabilityStatus availabilityText vipAvailabilityStatus vipAvailabilityText canAddToBag canAddToWishlist vipCanAddToBag onSale isNew ...ProductAttributes_Flags __typename } ...ProductVariant_Pricing __typename } fragment ProductVariant_Pricing on ProductVariant { price { formattedAmount centAmount currencyCode formattedValue __typename } listPrice { formattedAmount centAmount __typename } attributes { onSale __typename } __typename } fragment ProductAttributes_Flags on ProductAttributes { featuredFlags { key label __typename } __typename } fragment Variant_ReadOnlyProduct on ReadOnlyVariant { id sku attributes { featuredFlags { key label __typename } __typename } __typename } fragment Facet_FacetSidebar on Facet { name key id labels { __typename displayMode name labelKey count ... on FacetValue { value __typename } ... on FacetRange { from to __typename } } __typename } fragment Sort_SortOptions on SortOptions { id key direction label __typename } fragment ListingBanner on ListingBannerSection { title description contrast logoImage images { size url __typename } backgroundImages { small { url width height maxPixelDensity format __typename } medium { url width height maxPixelDensity format __typename } large { url width height maxPixelDensity format __typename } __typename } __typename } fragment CardContent on CardContentSection { moduleTitle showModuleTitle blocks { title isH1 description textAlignment logo secondaryLogo primaryLogoSrc { url width height maxPixelDensity format __typename } secondaryLogoSrc { url width height maxPixelDensity format __typename } logoPosition image imageSrc { url width height maxPixelDensity format __typename } callToActionText callToActionLink altText contrast __typename } __typename } fragment CopySectionData on CopySection { title showTitle body __typename } fragment QuickLinksData on QuickLinkSection { title quickLinks { title isH1 image link openInNewTab contrast imageSrc { url width height maxPixelDensity format __typename } __typename } __typename } fragment ContentBlockMixedData on ContentBlockMixed { moduleTitle showModuleTitle blocks { title isH1 description backgroundColor blockTheme contentPosition logoURL logoPosition callToActionText callToActionLink altText backgroundImages { largeImage { small { url width height maxPixelDensity format __typename } large { url width height maxPixelDensity format __typename } __typename } smallImage { small { url width height maxPixelDensity format __typename } large { url width height maxPixelDensity format __typename } __typename } __typename } __typename } __typename } fragment HeroBannerData on HeroBanner { heroblocks { id title isH1 tagline bannerTheme contentVerticalPosition contentHorizontalPosition contentHeight primaryLogoSrc primaryLogoSrcNew { url width height max